mapper.insert:
INSERT INTO T_MEMBER(
    ID
  , USERNAME
  , PASSWORD
) VALUES (
      MEMBER_SEQ.NEXTVAL
    , #{username}
    , #{password}
)

mapper.select:
SELECT COUNT(*)
    FROM T_MEMBER
    WHERE USERNAME = #{username}

SELECT ID
     , USERNAME
    FROM T_MEMBER
    WHERE USERNAME = #{username}
      AND PASSWORD = #{password}
